import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DossierService } from 'src/app/core/services/dossier.service';
import { DomSanitizer } from '@angular/platform-browser';
import Swal from 'sweetalert2';
import { MatDialog } from '@angular/material/dialog';
import { DisplayImageComponent } from '../display-image/display-image.component';
import { RendezvousService } from 'src/app/core/services/rendezvous.service';
import { EditDossierComponent } from '../edit-dossier/edit-dossier.component';
@Component({
  selector: 'app-views-dossier',
  templateUrl: './views-dossier.component.html',
  styleUrls: ['./views-dossier.component.scss']
})
export class ViewsDossierComponent implements OnInit {
  id = this.route.snapshot.params["id"]
  image:any
  constructor(
    private route : ActivatedRoute,
    private dossierService : DossierService,
    private _sanitizer : DomSanitizer,
    private dialog: MatDialog,
    private rendezvousService : RendezvousService,
    private router : Router
  ) { }

  ngOnInit(): void {
    this.getDossierByID()
    this.getImageByID()
    this.getRenvezVous()
  }
  dossier : any
  getDossierByID(){
    this.dossierService.getDossierById(this.id).subscribe(res=>{
      this.dossier = res
      console.log(this.dossier);

    })
  }
  rendezvous:any
  getRenvezVous(){
    this.rendezvousService.getAllRendezVousByDossier(this.id).subscribe(res=>{
      this.rendezvous = res
      console.log(res);

    })
  }
  show = false
  affectDisponsaire(){
    this.show = true
  }

  convert(base64String : any){
    return this._sanitizer.bypassSecurityTrustResourceUrl('data:image/jpg;base64,'+base64String);
  }

  imageTodisplay:any
  getImageByID(){
    this.dossierService.getImageById(this.id).subscribe(res=>{
      const response : any = res
      this.imageTodisplay = res
      if(response == null){
        this.image = "assets/images/pict-user.jpg"
      }else{
        console.log(response)
        this.image = this.convert(response.image)
      }


    })
  }
  selecetdFile:any
  onFileUpload(event: any) {
    this.selecetdFile = event.target.files[0];
    const fd = new FormData();
    fd.append("image", this.selecetdFile, this.selecetdFile.name)

    this.dossierService.saveImage(fd , this.id).subscribe(res=>{
      window.location.reload()
    })
  }

  displayImage(){
    this.dialog.open(DisplayImageComponent, {
      data: this.image
    });

  }

  capitalizeFirstLetter(str: string): string {
    if(str)
     return str?.charAt(0).toUpperCase() + str.slice(1);
  }

  openDialogEdit() {
    const dialogRef = this.dialog.open(EditDossierComponent, {
      data: this.dossier
    });
    dialogRef.afterClosed().subscribe(result => {
      this.getDossierByID()
      this.getImageByID()
      this.getRenvezVous()
    });
  }

  goToConsultation(id:any){
    const url = "consultations/"+id
    this.router.navigateByUrl(url)
  }

}
<div class="div-main">
  <div class="title-page">
    <h2>Dossier</h2>
  </div>
  <div style="padding: 15px">
    <div class="container">
      <div class="row">
        <div class="col-md-3">
          <div class="osahan-account-page-left shadow-sm bg-white h-100">
            <div class="border-bottom p-4">
              <div class="osahan-user text-center">
                <div class="osahan-user-media">
                  <img
                    (click)="displayImage()"
                    class="mb-3 rounded-pill shadow-sm mt-1"
                    style="width: 90px; height: 90px; border: 3px solid #2eb165"
                    [src]="image"
                    alt="gurdeep singh osahan"
                  />
                  <div style="color: #2eb165; cursor: pointer">
                    <mat-icon (click)="fileinput.click()">add_a_photo</mat-icon>
                  </div>
                  <input
                    type="file"
                    hidden
                    #fileinput
                    accept="image/*"
                    (change)="onFileUpload($event)"
                  />
                  <div class="osahan-user-media-body">
                    <h3 class="mb-2">
                      {{ capitalizeFirstLetter(dossier?.prenom) }}
                      {{ capitalizeFirstLetter(dossier?.nom) }}
                      <span class="badge badge-soft-success">{{
                        dossier?.numero
                      }}</span>
                    </h3>

                    <div>
                      <button
                        (click)="openDialogEdit()"
                        pButton
                        style="background-color: #2eb165"
                        icon="pi pi-pencil"
                        class="p-button-rounded"
                        severity="secondary"
