import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { RendezvousService } from 'src/app/core/services/rendezvous.service';

@Component({
  selector: 'app-view-rv',
  templateUrl: './view-rv.component.html',
  styleUrls: ['./view-rv.component.scss']
})
export class ViewRvComponent implements OnInit {

  constructor(
    private formBuilder: FormBuilder,
    @Inject(MAT_DIALOG_DATA) public data: any,
    public dialogRef: MatDialogRef<ViewRvComponent>,
    private rendezvousService :RendezvousService
  ) { }

  ngOnInit(): void {
    console.log(this.data);

  }

  delete(id:any){
    this.rendezvousService.rendezvousDalete(id).subscribe(res=>{
      this.dialogRef.close()
    })
  }

}

  <div class="w-100 title-dialog">
    <H2>Rendez vous</H2>
    <hr>
</div>


<div mat-dialog-content class="dialog-user">
    <div class="row" style="width: 700px;">
        <div class="col">
            <ul class="list-group">
                <li class="list-group-item">Date : {{data?.dateTime | date:"dd-MM-yyyy HH:mm"}}</li>
                <li class="list-group-item">Durée : {{data?.duree}}</li>
                <li class="list-group-item">Status : {{data?.statut}}</li>
                <li class="list-group-item"><strong>{{data?.typeRendezVous}}</strong></li>

              </ul>
        </div>
        <div class="col">
            <ul class="list-group">
                <li class="list-group-item">{{data?.dossier.prenom}} {{data?.dossier.nom}}</li>
                <li class="list-group-item">N°{{data?.dossier.numero}}</li>
                <li class="list-group-item">{{data?.disponibiliesMedecin.description}}</li>
                <li class="list-group-item" style="display : flex ;justify-content: right;">
                    <button class="btn btn-danger" (click)="delete(data.id)">Delete</button>
                </li>

              </ul>
        </div>
    </div>

</div>
