import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { DossierService } from 'src/app/core/services/dossier.service';
import { AddDossierComponent } from '../add-dossier/add-dossier.component';
import { MatDialog } from '@angular/material/dialog';
import { EditDossierComponent } from '../edit-dossier/edit-dossier.component';

@Component({
  selector: 'app-list-dossier',
  templateUrl: './list-dossier.component.html',
  styleUrls: ['./list-dossier.component.scss']
})
export class ListDossierComponent implements OnInit {
  dossiers:any
  constructor(
    private dossierService : DossierService,
    private router : Router,
    private dialog: MatDialog,
  ) { }

  ngOnInit(): void {
    this.getAllDossier()
  }

  getAllDossier(){
    this.dossierService.getAllDossiers().subscribe(res=>{
      this.dossiers = res
      console.log(res)
    })
  }

  openDialogAddDoc() {
    const dialogRef = this.dialog.open(AddDossierComponent, {
    });
    dialogRef.afterClosed().subscribe(result => {
      this.getAllDossier()
    });
  }

  capitalizeFirstLetter(str: string): string {
    return str?.charAt(0).toUpperCase() + str.slice(1);
  }

  showDetails(id : any){
    const url = "view-dossier/"+id
    this.router.navigateByUrl(url)
  }







}
<link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" rel="stylesheet" />
<div class="div-main">
    <div class="title-page">
        <nav class="navbar navbar-expand-lg bg-body-tertiary">
            <div class="container-fluid">
              <a class="navbar-brand" >Lists des dossiers</a>
              <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
              </button>
              <div class="collapse navbar-collapse" id="navbarSupportedContent">
                <ul class="navbar-nav me-auto mb-2 mb-lg-0">
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" >Homme</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" >Femme</a>
                  </li>

                </ul>
                <div style="display: flex;">
                  <input class="form-control me-2" type="search" placeholder="Recherche" aria-label="Search">
                  <button class="btn btn-outline-success" (click)="openDialogAddDoc()">Ajouter</button>
                </div>
              </div>
            </div>
          </nav>

    </div>


    <div class="container">


        <div class="row">
            <div *ngFor="let doss of dossiers" class="col-lg-4 col-md-6 col-12 mt-4 pt-2">
                <div class="card border-0 bg-light rounded shadow">
                    <div class="card-body p-4">
                        <span class="badge rounded-pill bg-primary float-md-end mb-3 mb-sm-0">{{doss.numero}}</span>
                        <h5>{{capitalizeFirstLetter(doss.nom)}} {{capitalizeFirstLetter(doss.prenom)}}</h5>
                        <div class="mt-3">
                            <span class="text-muted d-block"><i class="fa fa-phone" aria-hidden="true"></i> <a
                                    target="_blank" class="text-muted"> {{doss.telephone}}</a></span>
                            <span class="text-muted d-block"><i class="fa fa-map-marker" aria-hidden="true"></i>
                                {{capitalizeFirstLetter(doss.adresse)}}</span>
                        </div>

                        <div class="mt-3" (click)="showDetails(doss.id)">
                            <a class="btn btn-primary">Voir les détails</a>
                        </div>
                    </div>
                </div>
            </div><!--end col-->





        </div><!--end row-->
    </div>
</div>
