import { Component, OnInit } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog';
import { DoctorService } from 'src/app/core/services/doctor.service';
import Swal from 'sweetalert2';
class Doctor {
  nom: any;
  prenom: any;
  adresse: any;
  numeroTelephone: any;
  email: any;
  specialiteMedicales: any;
}
@Component({
  selector: 'app-add-doc',
  templateUrl: './add-doc.component.html',
  styleUrls: ['./add-doc.component.scss'],
})
export class AddDocComponent implements OnInit {
  specialiteMedicales: any;
  doctor = new Doctor();
  constructor(
    public dialogRef: MatDialogRef<AddDocComponent>,
    private doctorService: DoctorService
  ) {}

  ngOnInit(): void {
    this.getAllSpecialites();
  }

  getAllSpecialites() {
    this.doctorService.getAllspecialiteMedicale().subscribe((res) => {
      this.specialiteMedicales = res;
    });
  }
  responseDocAdd: any;
  onSubmit() {
    console.log(this.doctor);

    this.doctorService.createDoc(this.doctor).subscribe(
      (res) => {
        Swal.fire({
          title: 'Succès !',
          text: 'Le médcin a été ajouté avec succès.',
          icon: 'success',
          timer: 4000,
          timerProgressBar: true,
          showConfirmButton: false,
        }).then(() => {
          this.responseDocAdd = res;
          const obj = {
            idMedcin: this.responseDocAdd.id,
            joursEnums: [],
          };
          this.doctorService.createDispo(obj).subscribe((res) => {
            this.dialogRef.close();
            this.doctor = new Doctor();
          });
        });
      },
      (error) => {
        Swal.fire({
          title: 'Erreur !',
          text: "quelque chose s'est mal passé.",
          icon: 'error',
          timer: 4000,
          timerProgressBar: true,
          showConfirmButton: false,
        });
      }
    );
  }
}
<div class="w-100 title-dialog">
  <H2>Ajouter un Médicin</H2>
  <hr>
</div>


<div mat-dialog-content class="dialog-user">
  <form >
      <div class="row mb-1">
          <div class="col-xs-12 col-sm-12 col-md-12 col-lg-12 col-xl-12 col-xxl-12 mb-xs-3 mb-sm-3">
              <div class="row">
                <div class="col">
                  <mat-form-field appearance="outline">
                      <mat-label>Prénom</mat-label>
                      <input type="text"  name="prenom" [(ngModel)]="doctor.prenom" matInput placeholder="prenom">
                  </mat-form-field>
              </div>
                  <div class="col">
                      <mat-form-field appearance="outline">
                          <mat-label>Nom</mat-label>
                          <input name="nom" [(ngModel)]="doctor.nom" matInput placeholder="nom">
                      </mat-form-field>
                  </div>

              </div>
              <div class="row">
                  <div class="col">
                      <mat-form-field appearance="outline">
                          <mat-label>email</mat-label>
                          <input name="email" [(ngModel)]="doctor.email" matInput placeholder="email">
                      </mat-form-field>
                  </div>
                  <div class="col">
                      <mat-form-field appearance="outline">
                          <mat-label>numeroTelephone</mat-label>
                          <input name="numeroTelephone" [(ngModel)]="doctor.numeroTelephone" matInput placeholder="numeroTelephone">
                      </mat-form-field>
                  </div>
              </div>
              <div class="row">
                  <div class="col">
                      <mat-form-field appearance="outline">
                          <mat-label>adresse</mat-label>
                          <input name="adresse" [(ngModel)]="doctor.adresse" matInput placeholder="adresse">
                      </mat-form-field>
                  </div>
                  <div class="col">
                    <mat-form-field class="full-width" appearance="outline">
                      <mat-select placeholder="Specialite Medicales" name="specialiteMedicales" [(ngModel)]="doctor.specialiteMedicales" multiple>
                        <mat-option *ngFor="let sp of specialiteMedicales" [value]="sp">
                          {{ sp }}
                        </mat-option>
                      </mat-select>

                    </mat-form-field>
                  </div>
              </div>

          </div>
      </div>
  </form>

  <div class="row dialog-action">
      <button mat-raised-button class="btn-save btn-dialog-action" (click)="onSubmit()" color="primary">Ajout</button>
      <button mat-raised-button class="btn-cancel btn-dialog-action" color="warn" mat-dialog-close>Annuler</button>
  </div>
</div>
