                        </mat-form-field>
                    </div>
                    <div>
                        <button pButton pRipple label="Ajouter" (click)="openDialogAddDoc()" style="background-color: #2eb165;"  icon="pi pi-plus"
                        class="p-button-success p-button-rounded"></button>
                    </div>

                </div>
            </div>
        </div>
        <div class="row">
            <div class="col-lg-12">
                <div class="">
                    <div class="table-responsive">
                        <table class="table project-list-table table-nowrap align-middle table-borderless">
                            <thead>
                                <tr>

                                    <th scope="col">Pénom nom</th>
                                    <th scope="col">Numero</th>
                                    <th scope="col">Spécialités</th>

                                    <th scope="col">Email</th>
                                    <th scope="col" style="width: 200px;">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr *ngFor="let doctor of doctors">

                                    <td><img src="../../../../../assets/images/doctor.png"
                                            alt="" class="avatar-sm rounded-circle me-2" /><a
                                            class="text-body">{{capitalizeFirstLetter(doctor.name)}} </a></td>
                                    <td>{{doctor.code}}</td>
                                    <td>
                                        <div *ngFor="let i of doctor.specialiteMedicales" >

                                            <div class="badge badge-soft-primary mb-0">
                                                {{i}}
                                            </div>
                                        </div>
                                    </td>

                                    <td>
                                        <span class="badge badge-soft-success mb-0" >
                                            <td><i class="bi bi-envelope-fill"></i> {{doctor.email}}</td>
                                        </span>
                                    </td>
                                    <td>
                                        <button pButton (click)="openDialogEditDoc(doctor)"  icon="pi pi-pencil" class="p-button-rounded"
                                        severity="secondary"></button>
                                      <button pButton icon="pi pi-trash" (click)="delete(doctor.id)" style="margin-left: 2px;"
                                        class="p-button-rounded p-button-danger" severity="secondary"></button>
                                        <button pButton icon="pi pi-eye" style="margin-left: 2px;"
                                        class="p-button-rounded" severity="secondary" (click)="openDialogJours(doctor)"></button>
                                    </td>
                                </tr>


                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
